package iLTAssignments;

public class Iltclass1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		float b = 2;
//		float d = 0;
		char c;
		Boolean y = true;
		
		float add = a+b;
		System.out.println("addition of a and b is :" + add);
		float sub = a-b;
		System.out.println("Substraction of a and b is :" + sub);
		float div = a/b;
		System.out.println("Division of a and b is :" + div);
		float mul = a*b;
		System.out.println("Multiplication of a and b is :" + mul);
		a++;
		System.out.println("incremented value of a is :" + a);
		a--;
		System.out.println("Decremented value of a is :" + a);
	}

}
