package iTLClass5;

public class iTLClass51 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 10; 
		byte b = 5;
		short s = 6;
		long l = 8;
		float f = 10.5f;
		double d = 12.5;
		char c = 'm';
		boolean bol = true;
		
		Integer integer1 = new Integer(i);
		Byte byte1 = new Byte(b);
		Short short1 = new Short(s);
		Long long1 = new Long(l);
		Float float1 = new Float(f);
		Double double1 = new Double(d);
		Character char1 = new Character (c);
		Boolean boolean1 = new Boolean(bol);
		System.out.println("Integer value is :" + integer1);
		System.out.println("Byte value is :" + byte1);
		System.out.println("Short value is :" + short1);
		System.out.println("Long value is :" + long1);
		System.out.println("Float value is :" + float1);
		System.out.println("Double value is :" + double1);
		System.out.println("Character value is :" + char1);
		System.out.println("Boolean value is :" + boolean1);
	}

}
